def find_sequence_start(input,length):
    counter = 0
    i = 0
    while i < int(len(input)):
        seed = input[i]
        slice = input[i+1:i+length]
        if seed not in slice:
            counter += 1
            if counter == 4:
                print(i+1)
                print(input[i-length+1:i+1])
                break
        else:
            counter = 0
        i += 1

